import React, { useEffect , useState } from "react";
import image13 from '../images/image14.jpg'
import axios  from "axios";


export default function Card({product}) {


  const [users, setUsers] = useState([]);

  useEffect(() => {
    // Fetch data from the backend
    axios.get('http://localhost:3000/api/user')
        .then(response => {
            setUsers(response.data);
        })
        .catch(error => {
            console.error('Error fetching data:', error);
        });
}, []);
return (
  <div className="container mt-5">
    <div className="row">
      {users.map(user => (
        <div key={user._id} className="col-md-4">
          <div className="card mt-3" style={{ width: "18rem" }}>
            <img src={users} className="card-img-top" alt="User" />
            <div className="card-body">
              <h5 className="card-title">{users.productName} {user.productPrice}</h5>
              <p className="card-text">
                Email: {user.email}
              </p>
              <div className="container w-100">
                <select className="m-2 h-100 bg-success rounded">
                  {Array.from(Array(6), (e, i) => {
                    return (
                      <option key={i + 1} value={i + 1}>
                        {i + 1}
                      </option>
                    );
                  })}
                </select>
                <select className="m-2 h-100 bg-success rounded">
                  <option value="half">Half</option>
                  <option value="full">Full</option>
                </select>
                <div className="d-inline h-100">Total Price</div>
              </div>
              <hr />
              <button className="btn btn-success justify-center ms-2">Add to Cart</button>
            </div>
          </div>
        </div>
      ))}
    </div>
  </div>
);
}
