import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Card from '../../components/Card';

const DataStore = () => {
    const [products, set] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                // Make GET request to the backend API endpoint
                const response = await axios.get('http://localhost:3000/api/product/get_all_products'); // Replace '/api/data' with your actual API endpoint
                setData(response.data); // Update component state with fetched data
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData(); // Call the function to fetch data when component mounts
    }, []); // Empty dependency array ensures the effect runs only once
    return (
        <div className="container mt-5">
          <div className="row">
            {products.map((product) => (
              <div className="col-md-4" key={product.id}>
                <Card product={product} />
              </div>
            ))}
          </div>
        </div>
      );
    };

export default DataStore;
