import React, { useEffect, useState } from "react";
import axios from "axios";

import image13 from "../images/image14.jpg";

const Products = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch products from the backend
    const fetchProducts = async () => {
      try {
        const response = await axios.get(
          "http://localhost:3000/api/product/get_all_products"
        );
        setProducts(response.data.products);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  console.log(products);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }
  return (
    <div className="">
      {products.map((product) => (
        <div
          key={product._id}
          className="card mt-2 "
          style={{ width: "20rem", maxHeight: "500px" }}
        >


            <div className="fs-3 m-3  ">
{product.productCategory}
            </div>

          <img src={image13} className="card-img-top" alt="..." />
          <div className="card-body">
            <h5 className="card-title">{product.productName}</h5>
            <p className="card-text">
              {product.productDescription}
              <div className="container w-100">
                <select className=" m-2 h-100  bg-success rounded">
                  {Array.from(Array(6), (e, i) => {
                    return (
                      <option key={i + 1} value={i + 1}>
                        {i + 1}{" "}
                      </option>
                    );
                  })}
                </select>
                <select className=" m-2 h-100 bg-success rounded">
                  <option value="half">Half</option>
                  <option value="full">Full</option>
                </select>
                <div className="d-inline h-100 ">Total Price: {product.productPrice}</div>
              </div>
              <hr></hr>
              <button className={`btn btn-success justify-center ms-2`}>
                Add to Cart
              </button>
            </p>
          </div>
        </div>
      ))}
    </div>
  );
};



