import React, { useEffect, useState } from "react";
import axios from "axios";

import image13 from "../images/image14.jpg";

const Products = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [quantity , setQuantity] = useState(1);
  const[size , setSize] = useState("half");

  useEffect(() => {
    // Fetch products from the backend
    const fetchProducts = async () => {
      try {
        const response = await axios.get(
          "http://localhost:3000/api/product/get_all_products"

        );
        setProducts(response.data.products);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  //console.log(products);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }



  const cardCount = products.length;
  const columnSize = cardCount > 4 ? "col-6 col-md-4 col-lg-3" : `col-${12 / cardCount}`;


  const handleQuantityChange = (id, newQuantity) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product._id === id ? { ...product, quantity: newQuantity } : product
      )
    );
  };

  const handleSizeChange = (id, newSize) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product._id === id ? { ...product, size: newSize } : product
      )
    );
  };


  const calculateTotalPrice = (price, quantity, size) => {
    if (size === "full") {
      return price * quantity * 2; // Double the price for "Full" size
    } else {
      return price * quantity;
    }
  };

  return (
    <div className="container-fluid card-container">
      <div className="row">
        {products.map((product) => (
          <div key={product._id} className = {columnSize + " mb-4"}>
            <div className="card card-custom bg-dark">
              <div className="fs-3 m-3   text-white p-2 rounded">
                {product.productCategory}
              </div>
              <img src={image13} className="card-img-top img-fluid" />
              <div className="card-body">
                <h5 className="card-title text-white">{product.productName}</h5>
                <h className="card-title text-white">
                  {product.productDescription}
                </h>

                <p className="card-text">
                  {/* <div className="fs-3 m-3 bg-primary text-white p-2 rounded">
                    {product.productCategory}
                  </div> */}
                  <div className="container w-100">
                    <select className="m-2 h-100 bg-success rounded" onChange={handleQuantityChange}>
                      {Array.from(Array(5), (e, i) => (
                        <option key={i + 1} value={i + 1}>
                          {i + 1}
                        </option>
                      ))}
                    </select>
                    <select className="m-2 h-100 bg-success rounded" onChange={handleSizeChange}>
                      <option value="half">Half</option>
                      <option value="full">Full</option>
                    </select>
                    <hr style={{ borderTop: "1px solid white" }}></hr>
                    <div className=" h5 d-inline text-light">
                    Total Price:  Rs {calculateTotalPrice(product.productPrice, quantity, size)}
                    </div>
                  </div>

                  <hr style={{ borderTop: "1px solid white" }}></hr>
                  <button className="btn btn-success justify-center ms-2">
                    Add to Cart
                  </button>
                </p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Products;
