import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { CartContext } from "./cart/CartContext";
import image13 from "../images/image14.jpg";

const Products = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { addToCart } = useContext(CartContext);
  const [showMessage, setShowMessage] = useState(false); // State variable to track message display

  useEffect(() => {
    // Fetch products from the backend
    const fetchProducts = async () => {
      try {
        const response = await axios.get("http://localhost:3000/api/product/get_all_products");
        const productsWithState = response.data.products.map((product) => ({
          ...product,
          quantity: 1,
          size: "half",
        }));
        setProducts(productsWithState);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  const handleQuantityChange = (id, newQuantity) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product._id === id ? { ...product, quantity: newQuantity } : product
      )
    );
  };

  const handleIncreaseQuantity = (id) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product._id === id ? { ...product, quantity: product.quantity + 1 } : product
      )
    );
  };

  const handleDecreaseQuantity = (id) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product._id === id && product.quantity > 1
          ? { ...product, quantity: product.quantity - 1 }
          : product
      )
    );
  };

  const handleSizeChange = (id, newSize) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product._id === id ? { ...product, size: newSize } : product
      )
    );
  };

  const calculateTotalPrice = (price, quantity, size) => {
    return size === "full" ? price * quantity * 2 : price * quantity;
  };

  const handleAddToCart = (product) => {
    addToCart(product);
    setShowMessage(true); // Show message when item is added to cart
    setTimeout(() => {
      setShowMessage(false); // Hide message after a few seconds
    }, 3000);
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  const cardCount = products.length;
  const columnSize = cardCount > 4 ? "col-6 col-md-4 col-lg-3" : `col-${12 / cardCount}`;

  return (
    <div className="container-fluid card-container">
      <div className="row">
        {products.map((product) => (
          <div key={product._id} className={columnSize + " mb-4"}>
            <div className="card card-custom bg-dark">
              <div className="fs-3 m-3 text-white p-2 rounded">{product.productCategory}</div>
              <img src={image13} className="card-img-top img-fluid" />
              <div className="card-body">
                <h5 className="card-title text-white">{product.productName}</h5>
                <p className="card-title text-white">{product.productDescription}</p>
                <div className="container w-100">
                  <div className="m-2 h-100 bg-primary rounded " style={{width:'70px'}}>
                    <button className="btn btn-sm" style={{width:'0px'}} onClick={() => handleDecreaseQuantity(product._id)}>-</button>
                    <span className="mx-2">{product.quantity}</span>
                    <button className="btn  btn-sm"style={{width:'0px'}} onClick={() => handleIncreaseQuantity(product._id)}>+</button>
                  </div>
                  <select
                    className="m-2 h-100 bg-success rounded"
                    value={product.size}
                    onChange={(e) => handleSizeChange(product._id, e.target.value)}
                  >
                    <option value="half">Half</option>
                    <option value="full">Full</option>
                  </select>
                  <hr style={{ borderTop: "1px solid white" }}></hr>
                  <div className="h5 d-inline  text-light">
                    Total Price: Rs {calculateTotalPrice(product.productPrice, product.quantity, product.size)}
                  </div>
                  <hr style={{ borderTop: "1px solid white" }}></hr>
                  <button className="btn btn-success justify-center ms-2" onClick={() => handleAddToCart(product)}>
                    Add to Cart
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
      {showMessage && (
        <div className="modal">
          <div className="modal-content">
            <p>New item added to your cart!</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Products;
