import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/product/get_all_product');
        setOrders(response.data.orders);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  if (loading) {
    return <div className="container mt-5 text-center">Loading...</div>;
  }

  if (error) {
    return <div className="container mt-5 text-center">Error: {error}</div>;
  }

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">My Orders</h1>
      {orders.length === 0 ? (
        <div className="text-center">No orders found.</div>
      ) : (
        <div className="row row-cols-1 row-cols-md-2 g-4">
          {orders.map((order) => (
            <div key={order._id} className="col">
              <div className="card h-100 shadow-sm">
                <div className="card-body">
                  <h5 className="card-title">{order.product.productName}</h5>
                  <p className="card-text">Quantity: {order.quantity}</p>
                  <p className="card-text">Size: {order.size}</p>
                  <p className="card-text">Total Price: Rs.{order.totalPrice}</p>
                  <Link to={`/orders/${order._id}`} className="btn btn-primary btn-sm">
                    View Details
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Orders;
