import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import 'bootstrap/dist/css/bootstrap.min.css';
import { CartContext } from './CartContext';

const Navbar = () => {
    const { cart } = useContext(CartContext);

    // Function to handle logout
    const handleLogout = () => {
        // Clear user data from localStorage
        localStorage.removeItem('user');
        // Force a re-render to update the navbar
        window.location.reload();
    };

    // Get user data from local storage
    const user = JSON.parse(localStorage.getItem('user'));

    return (
        <div>
            <nav className="navbar navbar-expand-lg bg-success ">
                <div className="container-fluid">
                    <a className="navbar-brand" href="#">Go <span className='text-danger'>Food</span></a>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                            <li className="nav-item">
                                <a className="nav-link active" aria-current="page" href="#">Home</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="#">Link</a>
                            </li>
                        </ul>
                        <form className="d-flex" role="search">
                            {user ? (
                                <div className="dropdown">
                                    <button style={{ backgroundColor: 'black' }} className="btn btn-secondary dropdown-toggle" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                                        Welcome, {user.firstName}!
                                    </button>
                                    <ul className="dropdown-menu">
                                        <li><a className="dropdown-item" href="#">Profile</a></li>
                                        <li><a className="dropdown-item" href="#">Settings</a></li>
                                        <li><button className="dropdown-item" onClick={handleLogout}>Logout</button></li>
                                    </ul>
                                    <Link to={'/myorders'} className="btn btn-info ms-2">My Orders</Link>
                                    <Link to={'/mycart'} className="btn btn-warning ms-2">
                                        <FontAwesomeIcon icon={faShoppingCart} size='lg' />
                                        <span className="badge bg-danger">{cart.length}</span> {/* Cart count */}
                                    </Link>
                                </div>
                            ) : (
                                <>
                                    <Link to={'/login'} className="btn btn-primary" type="submit">Login</Link>
                                    <Link to={'/register'} className="btn btn-success ms-2" type="submit">Register</Link>
                                </>
                            )}
                        </form>
                    </div>
                </div>
            </nav>
        </div>
    );
};

export default Navbar;
