import React, { createContext, useState, useEffect } from 'react';

const CartContext = createContext();

const CartProvider = ({ children }) => {
  const [cart, setCart] = useState(() => {
    const savedCart = localStorage.getItem('cart');
    return savedCart ? JSON.parse(savedCart) : [];
  });

  const [orders, setOrders] = useState([]);

  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cart));
  }, [cart]);

  const addToCart = (product) => {
    setCart((prevCart) => {
      const existingProduct = prevCart.find(item => item && item._id === product._id);
      if (existingProduct) {
        return prevCart.map(item =>
          item && item._id === product._id
            ? { ...item, quantity: item.quantity + product.quantity }
            : item
        );
      } else {
        return [...prevCart, product];
      }
    });
  };

  const removeFromCart = (itemId) => {
    setCart(cart.filter(item => item && item._id !== itemId));
  };

  const addOrder = (product) => {
    setOrders((prevOrders) => [...prevOrders, product]);
  };

  return (
    <CartContext.Provider value={{ cart, orders, addToCart, removeFromCart, addOrder }}>
      {children}
    </CartContext.Provider>
  );
};

export { CartContext, CartProvider };
