import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { Link } from "react-router-dom"; // Import Link from react-router-dom
import { CartContext } from "./cart/CartContext";
import Modal from "./Modal";
import "../css/product/product.css";
import "../css/modal/modal.css";

const Products = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedProduct, setSelectedProduct] = useState(null); // State to hold selected product
  const { addToCart } = useContext(CartContext);
  const [showConfirmation, setShowConfirmation] = useState(false);

  useEffect(() => {
    // Fetch products from the backend
    const fetchProducts = async () => {
      try {
        const response = await axios.get(
          "http://localhost:3000/api/product/get_all_products"
        );
        const productsWithState = response.data.products.map((product) => ({
          ...product,
          quantity: 1,
          size: "half",
        }));
        setProducts(productsWithState);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  const handleQuantityChange = (id, newQuantity) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product._id === id ? { ...product, quantity: newQuantity } : product
      )
    );
  };

  const handleIncreaseQuantity = (id) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product._id === id
          ? { ...product, quantity: product.quantity + 1 }
          : product
      )
    );
  };

  const handleDecreaseQuantity = (id) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product._id === id && product.quantity > 1
          ? { ...product, quantity: product.quantity - 1 }
          : product
      )
    );
  };

  const handleSizeChange = (id, newSize) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product._id === id ? { ...product, size: newSize } : product
      )
    );
  };

  const calculateTotalPrice = (price, quantity, size) => {
    return size === "full" ? price * quantity * 2 : price * quantity;
  };

  const handleAddToCart = (product) => {
    addToCart(product);
  };
  // Function to handle the Buy Now action
  const handleBuyNow = (product) => {
    setSelectedProduct(product); // Set the selected product for confirmation
    setShowConfirmation(true); // Show the confirmation modal
  };

  // Function to close the confirmation modal
  const closeConfirmation = () => {
    setShowConfirmation(false);
  };

  // Function to confirm purchase and add to cart
  const confirmPurchase = () => {
    if (selectedProduct) {
      addToCart(selectedProduct);
      setShowConfirmation(false); // Close the confirmation modal after adding to cart
    }
  };

  // Function to handle modal opening and closing
  const openModal = (product) => {
    setSelectedProduct(product);
  };

  const closeModal = () => {
    setSelectedProduct(null);
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="container">
      <div className="row">
        {products.map((product) => (
          <div key={product._id} className="col-md-4">
            <div className="card mb-4">
              <button className="card-link" onClick={() => openModal(product)}>
                <img
                  src={`http://localhost:3000/products/${product.productImage}`}
                  className="card-img-top"
                  alt={product.productName}
                />
              </button>
              <div className="card-body">
                <h5 className="card-title">{product.productName}</h5>
                {/* <p className="card-text">{product.productDescription}</p> */}
                <div className="d-flex justify-content-between align-items-center">
                  <div className="btn-group">
                    <button
                      className="btn btn-sm btn-outline-secondary"
                      onClick={() => handleDecreaseQuantity(product._id)}
                    >
                      -
                    </button>
                    <span className="mx-2">{product.quantity}</span>
                    <button
                      className="btn btn-sm btn-outline-secondary"
                      onClick={() => handleIncreaseQuantity(product._id)}
                    >
                      +
                    </button>
                  </div>
                  <select
                    className="form-select form-select-sm"
                    value={product.size}
                    onChange={(e) =>
                      handleSizeChange(product._id, e.target.value)
                    }
                  >
                    <option value="half">Half</option>
                    <option value="full">Full</option>
                  </select>
                </div>
                <div className="d-flex justify-content-between align-items-center mt-2">
                  <div className="text-success">
                    Rs.
                    {calculateTotalPrice(
                      product.productPrice,
                      product.quantity,
                      product.size
                    )}
                  </div>
                  <button
                    className="btn btn-sm btn-primary"
                    onClick={() => handleAddToCart(product)}
                  >
                    Add to Cart
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Modal for displaying detailed product information */}
      {selectedProduct && (
        <Modal onClose={closeModal}>
          <div className="modal-header">
            <h5 className="modal-title">{selectedProduct.productName}</h5>
            <button
              type="button"
              className="btn-close"
              onClick={closeModal}
            ></button>
          </div>
          <div className="modal-body">
            <img
              src={`http://localhost:3000/products/${selectedProduct.productImage}`}
              className="img-fluid"
              alt={selectedProduct.productName}
            />
            <p>{selectedProduct.productDescription}</p>
            <div>
              <strong>Price:</strong> Rs.
              {calculateTotalPrice(
                selectedProduct.productPrice,
                selectedProduct.quantity,
                selectedProduct.size
              )}
            </div>

            <button
              className="btn btn-sm btn-primary mt-2"
              onClick={() => handleAddToCart(selectedProduct)}
            >
              Buy Now
            </button>
          </div>
        </Modal>
      )}


{showConfirmation && (
  <Modal onClose={closeConfirmation}>
    <div className="modal-header">
      <h5 className="modal-title">Confirm Purchase</h5>
      <button type="button" className="btn-close" onClick={closeConfirmation}></button>
    </div>
    <div className="modal-body">
      <p>Are you sure you want to buy {selectedProduct?.productName}?</p>
      <button className="btn btn-sm btn-primary" onClick={confirmPurchase}>Confirm</button>
      <button className="btn btn-sm btn-secondary" onClick={closeConfirmation}>Cancel</button>
    </div>
  </Modal>
)}
    </div>


  );
};

export default Products;
