import React from 'react'
import { Link } from 'react-router-dom'
import 'bootstrap/dist/css/bootstrap.min.css'






const Navbar = () => {


    //function to handle logout
const handleLogout = () =>{
    localStorage.removeItem('user');
    window.location.reload();
};

    // get user data from local storage
    const user = JSON.parse(localStorage.getItem('user'))

    return (
        <div>
            <nav className="navbar navbar-expand-lg bg-success ">
                <div className="container-fluid">
                    <a className="navbar-brand" href="#">Go <span className='text-danger'>Food</span></a>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                            <li className="nav-item">
                                <a className="nav-link active" aria-current="page" href="#">Home</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="#">Link</a>
                            </li>
                        </ul>
                        <form className="d-flex" role="search">
                            {user ? ( // Condition to check if user is logged in
                                <div className="dropdown">
                                    <button style={{ backgroundColor: 'black' }} className="btn btn-secondary dropdown-toggle" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                                        Welcome, {user.firstName}!
                                    </button>
                                    <ul className="dropdown-menu">
                                        <li><a className="dropdown-item" href="#">Profile</a></li>
                                        <li><a className="dropdown-item" href="#">Settings</a></li>
                                        <li><button className="dropdown-item" onClick={handleLogout}>Logout</button></li>
                                    </ul>
                                    <Link to={'/myorders'} className="btn btn-info ms-2">My Orders</Link> {/* Conditional rendering for My Orders button */}
                                </div>
                            ) : (
                                <> {/* Rendered when user is not logged in */}
                                    <Link to={'/login'} className="btn btn-primary" type="submit">Login</Link>
                                    <td></td>
                                    <Link to={'/register'} className="btn btn-primary" type="submit">Register</Link>
                                </>
                            )}
                        </form>
                    </div>
                </div>
            </nav>
        </div>
    );
};

export default Navbar
