import React, { useState } from 'react';
import image13 from '../images/image13.jpg';
import image11 from '../images/image11.jpg';
import image15 from '../images/image15.jpg';

export default function Carousel({ onSearch }) {
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    onSearch(searchQuery);
  };

  return (
    <div id="carouselExampleIndicators" className="carousel slide" style={{ objectFit: "contain !important" }}>
      <div className="carousel-indicators">
        <button type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide-to="0" className="active" aria-current="true" aria-label="Slide 1"></button>
        <button type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide-to="1" aria-label="Slide 2"></button>
        <button type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide-to="2" aria-label="Slide 3"></button>
      </div>

      <div className="carousel-caption d-none d-md-block" style={{ zIndex: "10" }}>
        <form className="d-flex" onSubmit={handleSearchSubmit}>
          <input className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" value={searchQuery} onChange={handleSearchChange} />
          <button className="btn btn-outline-success my-2 my-sm-0 text-white bg-success" type="submit">Search</button>
        </form>
      </div>

      <div className="carousel-inner" id='carousel'>
        <div className="carousel-item active" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '800px' }}>
          <img src={image13} className="d-block w-100" style={{ height: '100%', filter: 'brightness(30%)' }} alt="..." />
        </div>
        <div className="carousel-item">
          <img src={image11} className="d-block w-100" style={{ height: '100%', filter: 'brightness(30%)' }} alt="..." />
        </div>
        <div className="carousel-item">
          <img src={image15} className="d-block w-100" style={{ height: '100%', filter: 'brightness(30%)' }} alt="..." />
        </div>
      </div>
      <button className="carousel-control-prev" type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide="prev">
        <span className="carousel-control-prev-icon" aria-hidden="true"></span>
        <span className="visually-hidden">Previous</span>
      </button>
      <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide="next">
        <span className="carousel-control-next-icon" aria-hidden="true"></span>
        <span className="visually-hidden">Next</span>
      </button>
    </div>
  );
}
