import React from "react";
import Modal from "./Modal";

const ConfirmationModal = ({ show, onCancel, onConfirm }) => {
  return (
    <Modal onClose={onCancel} show={show}>
      <div className="modal-header">
        <h5 className="modal-title">Confirm Purchase</h5>
        <button type="button" className="btn-close" onClick={onCancel}></button>
      </div>
      <div className="modal-body">
        <p>Are you sure you want to purchase this item?</p>
      </div>
      <div className="modal-footer">
        <button className="btn btn-secondary" onClick={onCancel}>Cancel</button>
        <button className="btn btn-primary" onClick={onConfirm}>Yes</button>
      </div>
    </Modal>
  );
};

export default ConfirmationModal;
