import React from "react";

const Card = ({ product }) => {
  return (
    <div className="card mt-5" style={{ width: "18rem", maxHeight: "500px" }}>
      <img src={`http://localhost:3000/api/product/${product.productImage}`} className="card-img-top" alt="Product" />
      <div className="card-body">
        <h5 className="card-title">{product.productName}</h5>
        <p className="card-text">
          {/* Add product details */}
          <div className="container w-100">
            <select className="m-2 h-100 bg-success rounded">
              {Array.from(Array(6), (e, i) => {
                return (
                  <option key={i + 1} value={i + 1}>
                    {i + 1}
                  </option>
                );
              })}
            </select>
            <select className="m-2 h-100 bg-success rounded">
              <option value="half">Half</option>
              <option value="full">Full</option>
            </select>
            <div className="d-inline h-100 ">Total Price</div>
          </div>
          <hr />
          <button className="btn btn-success justify-center ms-2">Add to Cart</button>
        </p>
      </div>
    </div>
  );
};

export default Card;
