import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import 'bootstrap/dist/css/bootstrap.min.css';
import { CartContext } from './cart/CartContext';
import '../css/custom-navbar.css'; // Import custom CSS for navbar styling

const Navbar = () => {
    const { cart } = useContext(CartContext);

    // Function to handle logout
    const handleLogout = () => {
        // Clear user data from localStorage
        localStorage.removeItem('user');
        // Force a re-render to update the navbar
        window.location.reload();
    };

    // Get user data from local storage
    const user = JSON.parse(localStorage.getItem('user'));

    return (
        <nav className="navbar navbar-expand-lg navbar-dark bg-success">
            <div className="container-fluid">
                <Link className="navbar-brand" to="/">Go <span className="text-danger">Food</span></Link>
                <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>
                <div className="collapse navbar-collapse" id="navbarSupportedContent">
                    <ul className="navbar-nav ms-auto mb-2 mb-lg-0">
                        <li className="nav-item">
                            <Link className="nav-link active" to="/">Home</Link>
                        </li>
                        <li className="nav-item">
                            <Link className="nav-link" to="/menu">Menu</Link>
                        </li>
                    </ul>
                    <form className="d-flex">
                        {user ? (
                            <div className="dropdown">
                                <button className="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton" data-bs-toggle="dropdown" aria-expanded="false">
                                    Welcome, {user.firstName}!
                                </button>
                                <ul className="dropdown-menu dropdown-menu-end" aria-labelledby="dropdownMenuButton">
                                    <li><Link className="dropdown-item" to="/profile">Profile</Link></li>
                                    <li><Link className="dropdown-item" to="/settings">Settings</Link></li>
                                    <li><button className="dropdown-item" onClick={handleLogout}>Logout</button></li>
                                </ul>
                                <Link to="/myorders" className="btn btn-info ms-2">My Orders</Link>
                                <Link to="/mycart" className="btn btn-warning ms-2">
                                    <FontAwesomeIcon icon={faShoppingCart} size="lg" />
                                    <span className="badge bg-danger">{cart.length}</span> {/* Cart count */}
                                </Link>
                            </div>
                        ) : (
                            <>
                                <Link to="/login" className="btn btn-primary" type="submit">Login</Link>
                                <Link to="/register" className="btn btn-success ms-2" type="submit">Register</Link>
                            </>
                        )}
                    </form>
                </div>
            </div>
        </nav>
    );
};

export default Navbar;
