import React, { useEffect, useState } from "react";
import axios from "axios";
import image13 from "../images/image14.jpg";

const Products = ({ searchQuery }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [size, setSize] = useState("half");

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get("http://localhost:3000/api/product/get_all_products");
        setProducts(response.data.products);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  const handleQuantityChange = (e) => {
    setQuantity(parseInt(e.target.value));
  };

  const handleSizeChange = (e) => {
    setSize(e.target.value);
  };

  const calculateTotalPrice = (price, quantity, size) => {
    return size === "full" ? price * quantity * 2 : price * quantity;
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  const filteredProducts = products.filter((product) =>
    product.productName.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const cardCount = filteredProducts.length;
  const columnSize = cardCount > 4 ? "col-6 col-md-4 col-lg-3" : `col-${12 / cardCount}`;

  return (
    <div className="container-fluid card-container">
      <div className="row">
        {filteredProducts.map((product) => (
          <div key={product._id} className={columnSize + " mb-4"}>
            <div className="card card-custom bg-dark">
              <div className="fs-3 m-3 text-white p-2 rounded">{product.productCategory}</div>
              <img src={image13} className="card-img-top img-fluid" />
              <div className="card-body">
                <h5 className="card-title text-white">{product.productName}</h5>
                <p className="card-text text-white">{product.productDescription}</p>
                <div className="container w-100">
                  <select className="m-2 h-100 bg-success rounded" onChange={handleQuantityChange}>
                    {Array.from(Array(5), (e, i) => (
                      <option key={i + 1} value={i + 1}>
                        {i + 1}
                      </option>
                    ))}
                  </select>
                  <select className="m-2 h-100 bg-success rounded" onChange={handleSizeChange}>
                    <option value="half">Half</option>
                    <option value="full">Full</option>
                  </select>
                  <hr style={{ borderTop: "1px solid white" }}></hr>
                  <div className="h5 d-inline text-light">
                    Total Price: {calculateTotalPrice(product.productPrice, quantity, size)}
                  </div>
                  <hr style={{ borderTop: "1px solid white" }}></hr>
                  <button className="btn btn-success justify-center ms-2">Add to Cart</button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Products;
