import React, { useContext } from 'react';
import { CartContext } from ''

const CartPage = () => {
  const { cart } = useContext(CartContext);

  const calculateTotalPrice = (price, quantity, size) => {
    return size === "full" ? price * quantity * 2 : price * quantity;
  };

  if (cart.length === 0) {
    return <div className="container mt-5"><h2>Your cart is empty</h2></div>;
  }

  return (
    <div className="container mt-5">
      <h2>Your Cart</h2>
      <div className="row">
        {cart.map((item) => (
          <div key={item._id} className="col-12 mb-4">
            <div className="card card-custom bg-dark">
              <div className="fs-3 m-3 text-white p-2 rounded">{item.productCategory}</div>
              <img src={item.image || 'default-image-url.jpg'} className="card-img-top img-fluid" alt={item.productName} />
              <div className="card-body">
                <h5 className="card-title text-white">{item.productName}</h5>
                <p className="card-title text-white">{item.productDescription}</p>
                <div className="container w-100">
                  <div className="m-2 h-100 bg-primary rounded" style={{ width: '70px' }}>
                    <span className="mx-2">{item.quantity}</span>
                  </div>
                  <select
                    className="m-2 h-100 bg-success rounded"
                    value={item.size}
                    disabled
                  >
                    <option value="half">Half</option>
                    <option value="full">Full</option>
                  </select>
                  <hr style={{ borderTop: "1px solid white" }}></hr>
                  <div className="h5 d-inline text-light">
                    Total Price: Rs {calculateTotalPrice(item.productPrice, item.quantity, item.size)}
                  </div>
                  <hr style={{ borderTop: "1px solid white" }}></hr>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CartPage;
